<style lang="scss" scoped>
svg {
	--link-color: blue;
	--link-color: lightblue;
    --bg-color-HL: red;
}
@keyframes animmm {
	0% {
		transform: rotate(0);
	}

	to {
		transform: rotate(180deg);
	}
}
.kof-star {
	stroke: var(--link-color);
	fill: var(--bg-color-HL);
	stroke-width: 10;
	&:hover {
		animation: animmm 1s cubic-bezier(0.5, 0, 0.25, 1) alternate infinite;
	}
}
.active--exact {
	.kof-star {
		// fill: var(--bg-code);
		stroke: var(--link-dim);
	}
}
.kof-logo {
	fill: var(--link-color);
	fill-rule: nonzero;
	pointer-events: none;
}
.active--exact {
	.kof-logo {
		fill: var(--link-dim);
	}
}
</style>
